@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun PrescriptionSummarizeScreen(
 navController: NavController,
 prescriptionViewModel: PrescriptionViewModel = hiltViewModel()
) {
 val context = LocalContext.current
 val scrollBehavior = TopAppBarDefaults.enterAlwaysScrollBehavior(rememberTopAppBarState())
 val uiState by prescriptionViewModel.uiState.collectAsState()

 var showDialog by remember { mutableStateOf(false) }
 var imageUri by remember { mutableStateOf<Uri?>(null) }
 var cameraUri by remember { mutableStateOf<Uri?>(null) }

 val createImageFile = {
 val timeStamp = SimpleDateFormat("yyyyMMdd_HHmmss", Locale.getDefault()).format(Date())
 val imageFileName = "JPEG_${timeStamp}_"
 val storageDir = context.getExternalFilesDir(null)
 File.createTempFile(imageFileName, ".jpg", storageDir)
 }

 val cameraLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.TakePicture(),
 onResult = { success ->
 if (success) {
 imageUri = cameraUri
 }
 }
 )

 val galleryLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.GetContent(),
 onResult = { uri ->
 imageUri = uri
 }
 )

 val handleTakePhoto = {
 val photoFile = createImageFile()
 val photoUri = FileProvider.getUriForFile(
 context,
 "${context.packageName}.fileprovider",
 photoFile
 )
 cameraUri = photoUri
 cameraLauncher.launch(photoUri)
 showDialog = false
 }

 val handleAddImage = {
 galleryLauncher.launch("image/*")
 showDialog = false
 }

 val handleRemoveImage = {
 imageUri = null
 cameraUri = null
 prescriptionViewModel.clearSummary()
 }

 val handleSummarize = {
 imageUri?.let { uri ->
 prescriptionViewModel.analyzePrescription(uri)
 }
 Unit
 }

 // Error dialog
 uiState.error?.let { error ->
 AlertDialog(
 onDismissRequest = { prescriptionViewModel.clearError() },
 title = { Text("Error") },
 text = { Text(error) },
 confirmButton = {
 TextButton(onClick = { prescriptionViewModel.clearError() }) {
 Text("OK")
 }
 }
 )
 }

 // Image picker dialog
 if (showDialog) {
 AlertDialog(
 onDismissRequest = { showDialog = false },
 text = {
 Column {
 TextButton(
 onClick = handleTakePhoto,
 modifier = Modifier.fillMaxWidth()
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.Start,
 modifier = Modifier.fillMaxWidth()
 ) {
 Icon(
 imageVector = Icons.Default.CameraAlt,
 contentDescription = "Take Photo"
 )
 Spacer(modifier = Modifier.width(12.dp))
 Text("Take Photo")
 }
 }

 TextButton(
 onClick = handleAddImage,
 modifier = Modifier.fillMaxWidth()
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.Start,
 modifier = Modifier.fillMaxWidth()
 ) {
 Icon(
 imageVector = Icons.Default.Photo,
 contentDescription = "Add Image"
 )
 Spacer(modifier = Modifier.width(12.dp))
 Text("Add Image")
 }
 }
 }
 },
 confirmButton = {}
 )
 }

 Scaffold(
 topBar = {
 MediumTopAppBar(
 modifier = Modifier.fillMaxWidth(),
 title = {
 Text(
 text = "Scan Prescription",
 maxLines = 1,
 overflow = TextOverflow.Ellipsis
 )
 },
 navigationIcon = {
 IconButton(onClick = { navController.popBackStack() }) {
 Icon(
 imageVector = Icons.AutoMirrored.Filled.ArrowBack,
 contentDescription = "Back"
 )
 }
 },
 scrollBehavior = scrollBehavior,
 colors = TopAppBarDefaults.topAppBarColors(
 containerColor = MaterialTheme.colorScheme.background,
 scrolledContainerColor = MaterialTheme.colorScheme.background
 )
 )
 }
 ) { paddingValues ->
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 .padding(horizontal = 16.dp)
 .verticalScroll(rememberScrollState()),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Spacer(modifier = Modifier.height(16.dp))

 // Image upload section
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(300.dp),
 contentAlignment = Alignment.Center
 ) {
 Canvas(
 modifier = Modifier.fillMaxSize()
 ) {
 val strokeWidth = 2.dp.toPx()
 val dashLength = 10.dp.toPx()
 val gapLength = 8.dp.toPx()

 drawRoundRect(
 color = Color.Gray,
 style = Stroke(
 width = strokeWidth,
 pathEffect = PathEffect.dashPathEffect(
 floatArrayOf(dashLength, gapLength)
 )
 ),
 cornerRadius = CornerRadius(12.dp.toPx())
 )
 }

 if (imageUri != null) {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(8.dp)
 ) {
 AsyncImage(
 model = imageUri,
 contentDescription = "Selected prescription image",
 modifier = Modifier
 .width(240.dp)
 .height(240.dp)
 .clip(RoundedCornerShape(20.dp)),
 contentScale = ContentScale.Crop
 )

 FloatingActionButton(
 onClick = handleRemoveImage,
 modifier = Modifier
 .align(Alignment.TopEnd)
 .padding(8.dp)
 .size(32.dp),
 containerColor = MaterialTheme.colorScheme.error
 ) {
 Icon(
 imageVector = Icons.Default.Close,
 contentDescription = "Remove image",
 tint = MaterialTheme.colorScheme.onError,
 modifier = Modifier.size(18.dp)
 )
 }
 }
 } else {
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Center,
 modifier = Modifier.padding(16.dp)
 ) {
 Text(
 text = stringResource(R.string.upload_a_prescription),
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold,
 textAlign = TextAlign.Center
 )

 Spacer(modifier = Modifier.height(16.dp))

 Text(
 text = stringResource(R.string.take_a_photo_of_your_prescription_or_upload_an_existing_image),
 style = MaterialTheme.typography.bodyMedium,
 textAlign = TextAlign.Center,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )

 Spacer(modifier = Modifier.height(32.dp))

 Button(
 onClick = { showDialog = true },
 colors = ButtonDefaults.buttonColors(
 containerColor = MaterialTheme.colorScheme.surfaceVariant
 ),
 modifier = Modifier.width(120.dp)
 ) {
 Text(
 text = "Upload",
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }
 }
 }
 }

 Spacer(modifier = Modifier.height(24.dp))

 Button(
 onClick = handleSummarize,
 modifier = Modifier.fillMaxWidth().height(50.dp),
 enabled = imageUri != null && !uiState.isLoading
 ) {
 if (uiState.isLoading) {
 CircularProgressIndicator(
 modifier = Modifier.size(20.dp),
 color = MaterialTheme.colorScheme.onPrimary
 )
 Spacer(modifier = Modifier.width(8.dp))
 Text(
 text = "Analyzing...",
 color = Color.White
 )
 } else {
 Text("Summarize")
 }
 }

 // Display summary result
 uiState.summary?.let { summary ->
 Spacer(modifier = Modifier.height(24.dp))

 // Summary Card
 Card(
 modifier = Modifier.fillMaxWidth(),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.surfaceVariant
 )
 ) {
 Column(
 modifier = Modifier.padding(16.dp)
 ) {
 Text(
 text = "Prescription Summary",
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )

 Spacer(modifier = Modifier.height(12.dp))

 if (summary.summary.isNotEmpty()) {
 Text(
 text = summary.summary,
 style = MaterialTheme.typography.bodyMedium,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 Spacer(modifier = Modifier.height(16.dp))
 }

 if (summary.medications.isNotEmpty()) {
 Text(
 text = "Medications:",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.SemiBold,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 Spacer(modifier = Modifier.height(8.dp))

 summary.medications.forEach { medication ->
 MedicationCard(medication = medication)
 Spacer(modifier = Modifier.height(8.dp))
 }
 }

 if (summary.dosageInstructions.isNotEmpty()) {
 Spacer(modifier = Modifier.height(16.dp))
 Text(
 text = "Instructions:",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.SemiBold,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 Spacer(modifier = Modifier.height(8.dp))

 summary.dosageInstructions.forEach { instruction ->
 Text(
 text = "• $instruction",
 style = MaterialTheme.typography.bodyMedium,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }
 }

 // Warnings
 if (summary.warnings.isNotEmpty()) {
 Spacer(modifier = Modifier.height(16.dp))
 Card(
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.errorContainer
 )
 ) {
 Column(modifier = Modifier.padding(12.dp)) {
 Text(
 text = "⚠️ Important Warnings:",
 style = MaterialTheme.typography.titleSmall,
 fontWeight = FontWeight.SemiBold,
 color = MaterialTheme.colorScheme.onErrorContainer
 )
 Spacer(modifier = Modifier.height(4.dp))

 summary.warnings.forEach { warning ->
 Text(
 text = "• $warning",
 style = MaterialTheme.typography.bodySmall,
 color = MaterialTheme.colorScheme.onErrorContainer
 )
 }
 }
 }
 }
 }
 }
 }

 Spacer(modifier = Modifier.height(16.dp))
 }
 }
}

@Composable
private fun MedicationCard(medication: Medication) {
 Card(
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.surface
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
 ) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(12.dp)
 ) {
 Text(
 text = medication.name,
 style = MaterialTheme.typography.titleSmall,
 fontWeight = FontWeight.SemiBold,
 color = MaterialTheme.colorScheme.onSurface
 )

 if (medication.dosage.isNotEmpty()) {
 Text(
 text = "Dosage: ${medication.dosage}",
 style = MaterialTheme.typography.bodySmall,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }

 if (medication.frequency.isNotEmpty()) {
 Text(
 text = "Frequency: ${medication.frequency}",
 style = MaterialTheme.typography.bodySmall,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }

 if (medication.duration.isNotEmpty()) {
 Text(
 text = "Duration: ${medication.duration}",
 style = MaterialTheme.typography.bodySmall,
 color = MaterialTheme.colorScheme.onSurfaceVariant
 )
 }
 }
 }
