import androidx.navigation.NavController
import com.aritradas.medai.navigation.Screens
import com.aritradas.medai.ui.presentation.prescription.component.PrescriptionCard

@OptIn(ExperimentalMaterial3Api::class, ExperimentalMaterial3ExpressiveApi::class)
@Composable
fun PrescriptionScreen(
 navController: NavController,
 viewModel: PrescriptionViewModel = hiltViewModel(),
 navigateToDetailsScreen:(id: String) -> Unit
) {

 val context = LocalContext.current
 val uiState by viewModel.uiState.collectAsState()

 var hasNotificationPermission by remember {
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 mutableStateOf(
 ContextCompat.checkSelfPermission(
 context,
 Manifest.permission.POST_NOTIFICATIONS
 ) == PackageManager.PERMISSION_GRANTED
 )
 } else {
 mutableStateOf(true)
 }
 }

 var hasCameraPermission by remember {
 mutableStateOf(
 ContextCompat.checkSelfPermission(
 context,
 Manifest.permission.CAMERA
 ) == PackageManager.PERMISSION_GRANTED
 )
 }

 var hasStoragePermission by remember {
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 mutableStateOf(
 ContextCompat.checkSelfPermission(
 context,
 Manifest.permission.READ_MEDIA_IMAGES
 ) == PackageManager.PERMISSION_GRANTED
 )
 } else {
 mutableStateOf(
 ContextCompat.checkSelfPermission(
 context,
 Manifest.permission.READ_EXTERNAL_STORAGE
 ) == PackageManager.PERMISSION_GRANTED
 )
 }
 }

 val permissionLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.RequestPermission(),
 onResult = { isGranted ->
 hasNotificationPermission = isGranted
 }
 )

 val cameraPermissionLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.RequestPermission(),
 onResult = { isGranted ->
 hasCameraPermission = isGranted
 }
 )

 val storagePermissionLauncher = rememberLauncherForActivityResult(
 contract = ActivityResultContracts.RequestPermission(),
 onResult = { isGranted ->
 hasStoragePermission = isGranted
 }
 )

 LaunchedEffect(Unit) {
 if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 permissionLauncher.launch(Manifest.permission.POST_NOTIFICATIONS)
 }

 // Request camera permission
 if (!hasCameraPermission) {
 cameraPermissionLauncher.launch(Manifest.permission.CAMERA)
 }

 // Request storage permission based on Android version
 if (!hasStoragePermission) {
 val storagePermission = if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
 Manifest.permission.READ_MEDIA_IMAGES
 } else {
 Manifest.permission.READ_EXTERNAL_STORAGE
 }
 storagePermissionLauncher.launch(storagePermission)
 }
 }

 LaunchedEffect(Unit) {
 viewModel.loadPrescriptions()
 }

 Scaffold(
 floatingActionButton = {
 FloatingActionButton(
 onClick = { 
 navController.navigate(
 "${Screens.PrescriptionSummarize.route}?hasCameraPermission=$hasCameraPermission&hasStoragePermission=$hasStoragePermission"
 )
 }
 ) {
 Icon(
 imageVector = Icons.Outlined.Add,
 contentDescription = "Add Prescription"
 )
 }
 }
 ) { paddingValues ->
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 ) {
 when {
 uiState.isLoading -> {
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 CircularProgressIndicator()
 }
 }

 uiState.prescriptions.isEmpty() -> {
 Box(
 modifier = Modifier
 .fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 val transition = rememberInfiniteTransition(
 label = "Prescription rotate"
 )
 val angle by transition.animateFloat(
 initialValue = 0f,
 targetValue = 360f,
 animationSpec = infiniteRepeatable(
 animation = tween(durationMillis = 10000, easing = LinearEasing),
 repeatMode = RepeatMode.Restart
 ),
 label = "Prescription animation"
 )
 Column(horizontalAlignment = Alignment.CenterHorizontally) {
 Box(contentAlignment = Alignment.Center) {
 Spacer(
 Modifier
 .graphicsLayer {
 rotationZ = angle
 }
 .clip(MaterialShapes.Cookie12Sided.toShape())
 .background(colorScheme.primaryContainer)
 .padding(32.dp)
 .size(100.dp)
 )
 Icon(
 imageVector = Icons.AutoMirrored.Outlined.Assignment,
 contentDescription = null,
 tint = colorScheme.onPrimaryContainer,
 modifier = Modifier
 .padding(32.dp)
 .size(100.dp)
 )
 }
 Text(
 text = "No Prescriptions saved",
 textAlign = TextAlign.Center,
 style = MaterialTheme.typography.titleLarge,
 modifier = Modifier.padding(8.dp)
 )
 Text(
 text = "Tap the + button to scan your first prescription",
 textAlign = TextAlign.Center,
 style = MaterialTheme.typography.bodyMedium,
 modifier = Modifier.padding(horizontal = 48.dp)
 )
 }
 }
 }

 else -> {
 LazyColumn(
 modifier = Modifier.fillMaxSize(),
 contentPadding = PaddingValues(16.dp),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 items(uiState.prescriptions) { prescription ->
 PrescriptionCard(
 prescription = prescription,
 onClick = {
 navigateToDetailsScreen(prescription.id)
 }
 )
 }
 }
 }
 }
 }
 }

 // Error dialog
 uiState.error?.let { error ->
 AlertDialog(
 onDismissRequest = { viewModel.clearError() },
 title = { Text("Error") },
 text = { Text(error) },
 confirmButton = {
 TextButton(onClick = { viewModel.clearError() }) {
 Text("OK")
 }
 }
 )
 }
}

